package main

import (
	"context"
	"fmt"
	"net"
	"time"

	"github.com/pion/dtls/v3"
	"github.com/polevpn/kcp"
)

func handleConn(sess *kcp.UDPSession) {

	count := 0

	time1 := time.Now().Unix()
	bytes := 0

	for {

		data := []byte("")

		bytes += len(data)
		sess.Write(data)

		buf := make([]byte, 4096)
		_, err := sess.Read(buf)

		if err != nil {
			fmt.Println("read fail,err=", err)
		}

		count++

		if count > 100000 {
			sess.Close()
			break
		}

		//time.Sleep(time.Second * 1)

	}

	time2 := time.Now().Unix()

	fmt.Println("rate=", int64(bytes)/(time2-time1))

}

func main() {
	// Prepare the IP to connect to
	addr := &net.UDPAddr{IP: net.ParseIP("127.0.0.1"), Port: 4444}

	config := &dtls.Config{
		InsecureSkipVerify: true,
		MTU:                1400,
	}

	// Connect to a DTLS server
	ctx, cancel := context.WithTimeout(context.Background(), 30*time.Second)

	defer cancel()

	conn, err := kcp.DialWithContext(ctx, addr, config)

	if err != nil {
		fmt.Println("dail fail,", err)
		return
	}
	defer conn.Close()

	conn.SetNoDelay(1, 20, 2, 0)
	conn.SetWindowSize(4096, 4096)

	fmt.Println("connected")

	// Simulate a chat session
	handleConn(conn)
}
