package main

import (
	"context"
	"fmt"
	"net"
	"time"

	"github.com/pion/dtls/v3"
	"github.com/polevpn/kcp"
)

func handleConn(sess *kcp.UDPSession) {

	count := 0

	time1 := time.Now().UnixNano()
	bytes := 0

	for {

		data := []byte("")

		bytes += len(data)
		sess.Write(data)

		buf := make([]byte, 4096)
		_, err := sess.Read(buf)

		if err != nil {
			fmt.Println("read fail,err=", err)
		}

		count++

		if count > 100000 {
			break
		}

	}

	time2 := time.Now().UnixNano()

	timeSpan := float64((time2 - time1)) / float64(1000000000)

	fmt.Println("total=", bytes, "time=", timeSpan, "rate=", uint64(float64(bytes)/timeSpan))

	time.Sleep(time.Second * 8)
	sess.Close()

}

func main() {
	// Prepare the IP to connect to
	addr := &net.UDPAddr{IP: net.ParseIP("127.0.0.1"), Port: 8888}

	config := &dtls.Config{
		InsecureSkipVerify: true,
		MTU:                1400,
	}

	// Connect to a DTLS server
	ctx, cancel := context.WithTimeout(context.Background(), 3*time.Second)

	defer cancel()

	conn, err := kcp.DialWithContext(ctx, addr, config)

	if err != nil {
		fmt.Println("dail fail,", err)
		return
	}
	defer conn.Close()

	conn.SetNoDelay(1, 10, 2, 1)
	conn.SetWindowSize(32, 32)
	conn.SetACKNoDelay(true)

	fmt.Println("connected")

	// Simulate a chat session
	handleConn(conn)
}
